#define M(n)    ((n)%1000000007)
// Since the answer may be very large, return it modulo 109 + 7.
class Solution {
public:
    int bt(vector<int>& nums, vector<map<int,int>>&dp, 
            int ni, int prev1, int prev2)
    {
        if(ni == nums.size())return 1;
        else if(dp[ni].count(prev1) != 0)return dp[ni][prev1];

        long long res = 0;
        for(int curv1 = prev1, curv2; curv1 < nums[ni]; ++curv1){
            curv2 = nums[ni] - curv1;
            if(prev2 < curv2)continue;
            res = M(res + bt(nums, dp, ni+1, curv1, curv2));
        }
        return dp[ni][prev1] = M(res);
    }
    int countOfPairs(vector<int>& nums) {
        /*
        The lengths of both arrays are n.
        arr1 is monotonically non-decreasing
        arr2 is monotonically non-increasing
        arr1[i] + arr2[i] == nums[i] for all 0 <= i <= n - 1.
        */
        int nlen = nums.size();
        vector<map<int,int>> dp(nlen);
        return bt(nums, dp, 0, -1, INT_MAX);
    }
};
