class Solution {
    public int countOfPairs(int[] nums) {
        int kMOD = 1000000007;
        int[][] dp = new int[nums.length][51];
        for (int i = 0; i < nums.length; i++) {
            for (int x = 0; x <= nums[i]; x++) {
                for (int j = 0; j <= x; j++) {
                    if (i == 0) {
                        dp[i][x] = 1;
                    } else {
                        if (nums[i] - x <= nums[i - 1] - j) {
                            dp[i][x] = (dp[i][x] + dp[i-1][j]) % kMOD;    
                        }
                    }
                }    
            }
        }
        int res = 0;
        for (int i = 0; i < 51; i++) {
            res = (res + dp[nums.length - 1][i]) % kMOD;
        }
        return res;
    }
}
