class Solution {
public:
    int MOD = 1e9 + 7;
    int dp[2010][2010];
    int n;
    int rec(int i, int prev, vector<int>& nums) {
        if (i == n) return 1;
        if (dp[i][prev] != -1) return dp[i][prev];

        int ans = 0;
        for (int j = prev; j <= nums[i]; ++j) {
            int arr2i = nums[i] - j;
            if (i == 0 || arr2i <= nums[i - 1] - prev) {
                ans = (ans + rec(i + 1, j, nums)) % MOD;
            }
        }

        return dp[i][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        n = nums.size();
        return rec(0, 0, nums);
    }
};
