class Solution {
private:
    const int MOD = 1e9 + 7;

    int f(int i, int l, int r, vector<int>& nums,vector<vector<vector<int>>>&dp) {
        if (i >= nums.size()) return 1;
            if(dp[i][l][r]!=-1)return dp[i][l][r];
        int res = 0;
        for (int j = 0; j <= nums[i]; j++) {
            if (j >= l && (nums[i] - j) <= r) {
                res = (res + f(i + 1, j, nums[i] - j, nums,dp)) % MOD;
            }
        }
        return dp[i][l][r]=res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int max_val = *max_element(nums.begin(), nums.end());
        int l = 0, r = max_val+1;
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(r+1,vector<int>(r+1,-1)));

         //cout<<dp[1][3][5];
        return f(0, l, r, nums,dp);
    }
};
