class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int md = 1e9 + 7;
        int n = nums.size();
        int cnt = 0;
        vector<vector<int>> dp(n, vector<int>(51, 0));// a[i] = j cnt
        for(int i = 0; i < n; i++) {
            for(int j = 0; j <= nums[i]; j++) {
                if(i == 0) {
                    dp[i][j] = (nums[i] >= j)? 1:0;
                }
                else {
                    int prev = nums[i-1];
                    for(int k = 0; k <= j; k++) {
                        if(prev - k >= 0 && nums[i] - j <= prev - k) {
                            dp[i][j] += dp[i-1][k];
                            dp[i][j] %= md;
                        }
                    }
                }
            }
        }
        for(int i =0 ; i <= 50; i++) {
            cnt += dp[n-1][i];
            cnt %= md;
        }
        return cnt;
    }
};
