class Solution {
public:
    long long M = 1e9+7;
    long long ret = 0;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        // dfs(nums, 0);
        vector<vector<long long>> dp(n, vector<long long>(51));
        for(int j= 0; j <= nums[0] ; j++){
            dp[0][j] = 1;
        }
        for(int i =1 ; i < n ; i++){
            for(int j = 0 ; j <= nums[i]; j++){

                for(int k = 0; k <= min(j, nums[i-1]); k++){
                    int p_ar2 = nums[i-1] - k;
                    int n_ar2 = nums[i] - j;
                    if(p_ar2<n_ar2)continue;
                    dp[i][j] += dp[i-1][k];
                    dp[i][j]%=M;
                }
            }
        }
        int ret = 0;
        for(int i = 0; i <= nums[n-1]; i++){
            ret+=dp[n-1][i];
            ret%=M;
        }
        return ret;
    }
};

