class Solution {
    const int MOD = 1e9+7;

    vector<vector<int>>dp;

    int solve(int idx, int prev, int &n, vector<int>&nums){
        if(idx == n) return 1;
        int ans = 0;
        if(dp[idx][prev] != -1) return dp[idx][prev];
        if(idx == 0){
            for(int i=0;i<=nums[idx];i++){
                if(i>=prev ){ 
                    ans += solve(idx+1,i,n,nums);
                    ans %=MOD;
                }

            }
        }
        else{
            for(int i=0;i<=nums[idx];i++){
                if(i >= prev && nums[idx-1] - prev >= nums[idx] - i){
                    ans += solve(idx+1,i,n,nums);
                    ans %=MOD;
                }
            }
        }
        return dp[idx][prev] = ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n,vector<int>(51,-1));
        return solve(0,0,n,nums);
    }
};
