class Solution {
    vector<vector<vector<int>>> dp;

    int solve(int i, int prevArr1, int prevArr2, const vector<int>& nums, const int MOD) {
        if (i == nums.size()) return 1; 
        if (dp[i][prevArr1][prevArr2] != -1) return dp[i][prevArr1][prevArr2];

        int count = 0;
        for (int x = 0; x <= nums[i]; x++) {
            int arr1_i = x;
            int arr2_i = nums[i] - x;

            if (arr1_i >= prevArr1 && arr2_i <= prevArr2) {
                count = (count + solve(i + 1, arr1_i, arr2_i, nums, MOD)) % MOD;
            }
        }

        return dp[i][prevArr1][prevArr2] = count;
    }
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();


        dp.assign(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return solve(0, 0, 50, nums, MOD);
    }
};
