#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();

        // DP table initialization
        vector<vector<int>> dp(n, vector<int>(51, 0));

        // Initialize the DP for the first element
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        // Fill the DP table
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                for (int k = 0; k <= nums[i-1]; ++k) {
                    if (k <= j && nums[i] - j <= nums[i-1] - k) {
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }

        // Sum up all possibilities for the last element
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
