class Solution {
public:
    #define ll long long int
    ll md=1e9+7;
    vector<int>a;
    vector<vector<ll>>dp;
    ll n;
    ll solve(ll i,ll pre)
    {
        if(i>=n)
            return(1);
        ll ans=0;
        if(dp[i][pre]!=-1)
            return(dp[i][pre]);
         for(ll j=0;j<=(ll)a[i];j++)
         {
             if(pre<=j&&(i==0||((ll)a[i-1]-pre)>=((ll)a[i]-j)))
             {
                 ans=(ans+solve(i+1,j))%md;
             }
         }
        dp[i][pre]=ans;
        return(ans);
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        a=nums;
        dp.resize(n,vector<ll>(51,-1));
        return(solve((ll)0,(ll)0));
    }
};
