class Solution {
public:
    long long int dp[2001][51][51];
    int MOD = 1e9+7;
    long long rec(int level, int num1, int num2, vector<int> &nums) {
        if(level >= nums.size()) return 1;
        long long cnt = 0;
        if(dp[level][num1][num2] != -1) return dp[level][num1][num2];
        for(int i=num1; i<=nums[level]; i++) {
            for(int j=num2; j>=0; j--) {
                int tot = i+j;
                if(tot == nums[level]) {
                    cnt = (cnt + rec(level+1, i, j, nums))%MOD;
                }
            }
        }
        return dp[level][num1][num2] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        int n = nums.size();
        return rec(0, 0, nums[0], nums);
    }
};
