class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        #define ll long long
        ll n = nums.size();
        ll mod = 1e9+7;
        int dp[n+3][55][55];
        memset(dp,-1,sizeof(dp));
        auto f = [&](int idx,int prev1,int prev2 ,auto &&f)->ll
        {
            if(idx>=n)
            {
                return 1;
            }
            if(dp[idx][prev1+1][prev2+1] != -1)
                return dp[idx][prev1+1][prev2+1];
            ll a =0;
            if(idx==0)
            {
                for(int i=0;i<=nums[idx];i++)
                {
                    a = (a%mod+f(idx+1,i,nums[idx]-i,f)%mod)%mod;
                }
            }
            else
            {
                 for(int i=0;i<=nums[idx];i++)
                 {
                     if(i>=prev1 && prev2 >= nums[idx]-i)
                     a= (a%mod+f(idx+1,i,nums[idx]-i,f)%mod)%mod;
                 }
            }

            return dp[idx][prev1+1][prev2+1]= a%mod;  
        };
       ll ans= f(0,-1,-1,f)%mod;
        return ans;
    }
};
