class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const long long MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n+5, vector<long long>(55, 0));
        long long ans = 0;
        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }
        int pref1, pref2, now1, now2;
        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= nums[i-1]; j++) {
                for(int k = j; k <= nums[i]; k++) {
                    pref1 = j;
                    pref2 = nums[i-1] - j;
                    now1 = k;
                    now2 = nums[i] - k;
                    if(pref1 <= now1 && pref2 >= now2) {
                        (dp[i][k] += dp[i-1][j]) %= MOD;
                    }
                }
            }
        }
        for(int i = 0; i <= nums.back(); i++) {
            (ans += dp[n-1][i]) %= MOD;
        }
        return ans;
    }
};
