class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length, MAXN = 1000, MOD = 1000000007;
        int[] dp = new int[MAXN + 1], sum = new int[MAXN + 1];
        for (int i = 0; i <= nums[0]; i++)
            dp[i] = 1;
        sum[0] = dp[0];
        for (int i = 1; i <= MAXN; i++)
            sum[i] = (sum[i - 1] + dp[i]) % MOD;
        for (int i = 1; i < n; i++) {
            Arrays.fill(dp, 0);
            for (int x = 0; x <= nums[i]; x++)
                dp[x] = nums[i - 1] - nums[i] + x >= 0 ? sum[Math.min(nums[i - 1] - nums[i] + x, x)] : 0;
            sum[0] = dp[0];
            for (int j = 1; j <= MAXN; j++)
                sum[j] = (sum[j - 1] + dp[j]) % MOD;
        }
        return sum[MAXN];
    }
}
