class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int M=1e9+7;
        int n = nums.size();
        vector<vector<int>> a(n, vector<int>(51, 0));
        for (int j=0;j<=nums[0];j++) {
            a[0][j]=1; 
        }

        for (int i=1;i<n;i++) {
        for (int j=0;j<=nums[i];j++) {
        for (int k=0;k<=j;k++) {
        if (nums[i]-j<=nums[i-1]-k) {
        a[i][j]=(a[i][j]+a[i-1][k])%M;
        }}}
        }
        int  ans=0;
        for (int j =0;j<=50;j++) {
            ans=(ans+a[n-1][j])%M;
        }


        return ans;
    }
};
