#define ll long long
class Solution
{
public:
    int countOfPairs(vector<int> &nums)
    {
        int n = nums.size();
        ll mod = 1e9 + 7;
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));

        for (int j = 0; j <= nums[0]; j++)
            dp[0][j][nums[0] - j] = 1;

        for (int i = 1; i < n; i++)
        {
            for (int j = 0; j <= 50; j++)
            {
                for (int k = 0; k <= 50; k++)
                {
                    if (dp[i - 1][j][k] > 0)
                    {
                        for (int first = j; first <= nums[i]; first++)
                        {
                            if (nums[i] - first <= k)
                            {
                                dp[i][first][nums[i] - first] = (dp[i][first][nums[i] - first] + dp[i - 1][j][k]) % mod;
                            }
                        }
                    }
                }
            }
        }

        int ans = 0;
        for (int j = 0; j <= 50; j++)
        {
            for (int k = 0; k <= 50; k++)
                ans = (ans + dp[n - 1][j][k]) % mod;
        }

        return ans;
    }
};
