class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD=1000000007
        n = len(nums)
        memo = {}

        def dp(i, last1, last2):
            if i == n:
                return 1

            if (i, last1, last2) in memo:
                return memo[(i, last1, last2)]

            res = 0

            for arr1_i in range(last1, nums[i] + 1):
                arr2_i = nums[i] - arr1_i
                if arr2_i <= last2:
                    res += dp(i + 1, arr1_i, arr2_i)
                    res %= MOD

            memo[(i, last1, last2)] = res
            return res

        return dp(0, 0, nums[0])
