class Solution {
    int mod=1_000_000_007;
    public int countOfPairs(int[] nums) {
        int dp[][][]=new int[nums.length][53][53];
        for(int i=0;i<dp.length;i++)
        for(int j=0;j<dp[0].length;j++)
        Arrays.fill(dp[i][j],-1);
        return call(0,0,52,nums,dp);
    }
    public int call(int index,int pnum1,int pnum2,int nums[],int dp[][][]){
        if(index>=nums.length){
            return 1;
        }
        if(dp[index][pnum1][pnum2]!=-1)return dp[index][pnum1][pnum2];
        int ans=0;
        for(int first=pnum1;first<=nums[index];first++){
            int second=nums[index]-first;
            if(second<=pnum2 && first+second==nums[index]){
                ans=(ans%mod+call(index+1,first,second,nums,dp)%mod)%mod;
            }
        }
        return dp[index][pnum1][pnum2]=ans%mod;

    }
}
