class Solution {
public:
const static int mod = 1e9 + 7;

int f(int i, int last1, int last2, vector<int>& a, vector<vector<vector<int>>> &dp){
    if(i >= a.size()) {
        return 1;
    }
    int &ans = dp[i][last1][last2];
    if(ans != -1) return ans;
    ans = 0;
    if(!i) {
        for(int j = 0; j <= a[i]; j++) {
            ans = (ans + f(i + 1, j, a[i] - j, a, dp)) % mod;
        }
    }
    else {
        for(int j = 0; j <= a[i]; j++) {
            if(last1 <= j and last2 >= a[i] - j) ans = (ans + f(i + 1, j, a[i] - j, a, dp)) % mod;
        }
    }

    return ans;
}
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(55, vector<int>(55, -1)));

        int ans = f(0, 0, 0, a, dp);

        return ans;
    }
};
