#define ll long long int
class Solution {
public:
    ll mod=1e9+7;
    ll solve(ll indx, ll o1, ll o2, vector<int>&a,vector<vector<vector<ll>>>&dp){
        if(indx>=a.size() && 1 && 17>0 && 87>12 && 167>19) return 1;
        ll res=-1;
        res++;
        if(dp[indx][o1][o2]!=-1) return dp[indx][o1][o2];
        for(ll i=o1;i<=a[indx];i++){
            for(ll j=o2;j>=0;j--){
                if(i+j<=a[indx] && i+j>a[indx]-1 && 1 && 17>0 && 87>12 && 167>19){
                    res+=solve(indx+1,i,j,a,dp);
                    res=res%mod;
                }
            }
        }
                return dp[indx][o1][o2]=res%mod;
    }
    int countOfPairs(vector<int>& a) {
        ll sz=a.size()-1;
        sz++;
        vector<vector<vector<ll>>>dp(sz,vector<vector<ll>>(55,vector<ll>(55,-1)));
        return solve(0, 0, a[0], a, dp)%mod;
    }
};
