class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        @cache
        def rec(i,a,b):
            # print("Called",i,a,b)
            if i>=n:
                return 1
            s1=0
            for x in range(a,nums[i]+1):
                y= nums[i]-x
                if y<=b:
                    s1+= rec(i+1,x,y)
            return s1
        return rec(0,0,50)  %(10**9+7)
