class Solution {
private:
    int mod=1e9+7;
    int findCount(int ind,int greaterThan,int lessThan,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(ind==nums.size()) return 1;
        if(dp[ind][greaterThan][lessThan]!=-1) return dp[ind][greaterThan][lessThan];
        int count=0;
        for(int val=greaterThan;val<=nums[ind];val++){
            if(val>=greaterThan && (nums[ind]-val)<=lessThan){
                count = (count+findCount(ind+1,val,nums[ind]-val,nums,dp)%mod)%mod;
            }
        }
        return dp[ind][greaterThan][lessThan]=count%mod;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return findCount(0,0,50,nums,dp);
        //arr1=increasing
        //arr2=decreasing
    }
};
