class Solution {
public:

    int dp[2005][55];

    int countOfPairs(vector<int>& nums) {
        for (int i = 0; i <= nums[0]; ++i) dp[0][i] = 1;
        const int n = nums.size();
        for (int i = 1; i < nums.size(); ++i) {
            int diff = nums[i] - nums[i-1];
            for (int j = 0; j <= nums[i]; ++j) {
                int ll = max(j + diff, j);
                int rr = nums[i];
                for (int k = ll; k <= rr; ++k) {
                    dp[i][k] += dp[i-1][j];
                    dp[i][k] %= 1000000007;
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < 55; ++i) {
            ans += dp[n-1][i];
            ans %= 1000000007;
        }
        return ans;
    }
};
