class Solution {
    static long mod=1000000007;
    public static long solve(int nums[],int i,int aprev,int bprev,long dp[][][])
    {
        if(i==nums.length) return 1;
        if(dp[i][aprev][bprev]!=-1) return dp[i][aprev][bprev];
        long count=0;
        for(int j=aprev;j<=nums[i];j++)
        {
            int b=nums[i]-j;
            // System.out.println("Before "+i+" "+j+" "+b);
            // System.out.println(i+" "+j+" "+b);
            if(b<=bprev) count=(count+solve(nums,i+1,j,b,dp))%mod;
        }
        return dp[i][aprev][bprev]=count;
    }
    public int countOfPairs(int[] nums) {
        long dp[][][]=new long[nums.length][52][52];
        for(long a[][]:dp)
        {
            for(long b[]:a) Arrays.fill(b,-1);
        }
        return (int)solve(nums,0,0,51,dp);
    }
}
