class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)
        M = 10**9 + 7
        @cache
        def dfs(idx, pre_dec, pre_inc):
            if idx == n:
                return 1
            res = 0
            for inc in range(pre_inc, nums[idx]+1):
                dec = nums[idx] - inc
                if dec > pre_dec:
                    continue
                res = (res + dfs(idx+1, dec, inc)) % M 
            return res

        return dfs(0,float('inf'),0)
