#include <vector>
#include <cstring>
using namespace std;

class Solution {
public:
    static const int MOD = 1e9 + 7;
    int dp[2001][51][51];

    int solveRec(vector<int>& nums, int index, int prev1, int prev2) {
        if (index == nums.size()) return 1; 

        if (dp[index][prev1][prev2] != -1) {
            return dp[index][prev1][prev2]; 
        }

        int count = 0; 
        for (int i = 0; i <= nums[index]; i++) {
            int j = nums[index] - i; 

            if (i >= prev1 && j <= prev2) {
                count += solveRec(nums, index + 1, i, j);
                count %= MOD; 
            }
        }

        return dp[index][prev1][prev2] = count; 
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp)); 
        return solveRec(nums, 0, 0, 50); 
    }
};
