const int MOD = 1e9 + 7;

int dp[2001][51][51];

class Solution {
public:
    int solve(int i, int l1, int l2, vector<int>& nums)
    {
        if (dp[i][l1][l2] != -1) return dp[i][l1][l2];
        if (i == nums.size())
            return 1;

        int res = 0;
        for (int a = l1; a <= 50; a++)
        {
            for (int b = l2; b >= 0; b--)
            {
                if (a + b == nums[i])
                    res = (res + solve(i + 1, a, b, nums)) % MOD;
            }
        }

        return dp[i][l1][l2] = res;
    }

    int countOfPairs(vector<int>& nums) {
        int res = 0;

        memset(dp, -1, sizeof(dp));
        for (int a = 0; a <= 50; a++)
        {
            for (int b = 0; b <= 50; b++)
            {
                if (a + b == nums[0])
                {
                    res = (res + solve(1, a, b, nums)) % MOD;
                }
            }
        }

        return res;
    }
};
