MOD = 10**9+7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0]*51 for _ in range(n)]
        # print(dp)
        for i in range(nums[0]+1):
            dp[0][i] = 1
        for k in range(1, n):
            for i in range(nums[k]+1):
                for j in range(i+1):
                    u = nums[k-1] - j
                    if u < nums[k]-i:
                        break
                    dp[k][i] = (dp[k][i] + dp[k-1][j]) % MOD

        return sum(dp[-1]) % MOD
