#define f(i, a, b) for (ll i = a; i < b; i++)
#define ll long long
#define mod (int)(1e9 + 7)

class Solution
{
public:
    ll fun(int i, int prea, int preb, vector<vector<vector<int>>> &dp, vector<int> &nums)
    {
        if (i == nums.size())
            return 1;

        if (dp[i][prea][preb] != -1)
            return dp[i][prea][preb];

        ll ctn = 0;
        for (int k = 0; k <= nums[i]; k++)
        {   
            if(prea<=k &&  preb>=(nums[i]-k))
               ctn = (ctn + fun(i + 1,  k, nums[i] - k, dp, nums)) % mod;
        }

        return dp[i][prea][preb] = ctn;
    }

    int countOfPairs(vector<int> &nums)
    {

        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));

        return fun(0, 0, 51, dp, nums);
    }
};
