class Solution {
public:
    int mod = 1e9 + 7;
    #define vvvi vector<vector<vector<int>>>

    int zz(int ind, int preva1, int preva2, vector<int>& nums, vvvi& dp) {
        if (ind >= nums.size()) 
        return 1;

        if (dp[ind][preva1][preva2] != -1) 
        return dp[ind][preva1][preva2];

        int ans = 0;
        for (int i = preva1; i <= nums[ind]; i++) {
            if (i >= preva1 && (nums[ind] - i) <= preva2) {
                ans = (ans + zz(ind + 1, i, nums[ind] - i, nums, dp)) % mod;
            }
        }
        return dp[ind][preva1][preva2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int maxval = *max_element(nums.begin(), nums.end());
        vvvi dp(nums.size(), vector<vector<int>>(maxval + 1, vector<int>(maxval + 1, -1)));
        return zz(0, 0, maxval, nums, dp);
    }
};
