class Solution {
public:
    long long int solve(int a, int b, int c, vector<int>& d, vector<vector<vector<long long int>>>& e) {
         if (a >= d.size()) return 1;
        long long int f = 0;
        long long int g = 1e9 + 7;
        if (e[a][b][c] != -1) return e[a][b][c];

        for (int h = b; h <= d[a]; h++) {
            for (int i = c; i >= 0; i--) {
                if (h + i == d[a]) {
                    f += solve(a + 1, h, i, d, e);
                    f = f % g;
                }
            }
        }
        return e[a][b][c] = f;
    }

    int countOfPairs(vector<int>& d) {
        int n = d.size();
        //RRRRRRRRR
        vector<vector<vector<long long int>>> e(n, vector<vector<long long int>>(55, vector<long long int>(55, -1)));
        return solve(0, 0, d[0], d, e);
    }
};
