#define mod (int) (1e9 + 7)
class Solution {
public:
    int f(int i , int prev_mn , int prev_mx , vector<int> & v , vector<vector<vector<int> > > & dp){
        int n = v.size();
        if(i >= n){
            return 1;
        }
        if(dp[i][prev_mn][prev_mx] != - 1){
            return dp[i][prev_mn][prev_mx];
        }
        int ans = 0;
        for(int r = v[i] - prev_mx ; r <= v[i] ; r ++){
            int x = r , y = v[i] - r;
            if(x < prev_mn || y > prev_mx){
                continue;
            }
            ans = (ans + f(i + 1 , x , y , v , dp)) % mod;
        }
        return dp[i][prev_mn][prev_mx] = ans;

    }
    int countOfPairs(vector<int>& v) {
        int n = v.size();
        int mx = *max_element(v.begin() , v.end());
        vector<vector<vector<int> > > dp(n , vector<vector<int> > (mx + 1 , vector<int> (mx + 1 , -1)));
        return f(0 , 0 , v[0] , v , dp);
    }
};
