class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mem = {}
        def solve(lower, upper, index):
            if index >= len(nums):
                return 1
            if lower > nums[index]:
                return 0
            if (lower, upper, index) in mem:
                return mem[(lower, upper, index)]
            else:
                res = sum(
                    [
                        solve(x, nums[index] - x, index + 1)
                        for x in range(max(lower, nums[index] - upper), nums[index] + 1)
                    ]
                ) % (10**9 + 7)
                mem[(lower, upper, index)] = res
                return res
        return solve(0, nums[0], 0)
