class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        options = [(i, nums[0] - i, 1) for i in range(nums[0] + 1)]
        n = len(nums)
        for i in range(1, n):
            newOptions = []
            for j in range(nums[i] + 1):
                k = nums[i] - j
                count = 0
                for option in options:
                    if j >= option[0] and k <= option[1]:
                        count += option[2]
                if count > 0:
                    newOptions.append((j, k, count))
            options = newOptions
        return sum([option[2] for option in options]) % (10**9 + 7)
