class Solution {
public:
    int MOD=1000000007;
    int n;
    int dp[2005][55];
    int rec(int i,int prev1,int prev2,vector<int> &nums){
        if(i == n)return 1;
        if(dp[i][prev1]== -1){
            int ans=0;
            for(int j=prev1;j<=nums[i];j++){
                if(nums[i]-j <= prev2){
                    ans = ((ans + rec(i+1,j,nums[i]-j,nums))%MOD + MOD)%MOD;
                }
            }
            dp[i][prev1]=ans;
        }
        return dp[i][prev1];
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        for(int i=0;i<=n+1;i++){
            for(int j=0;j<=51;j++){
                dp[i][j]=-1;
            }
        }
        int ans=rec(0,0,nums[0],nums);
        return ans%MOD;
    }
};
