class Solution {
public:
    int h(vector<int>& nums, vector<int>& arr1, vector<int>& arr2, int i, vector<vector<int>>& dp) {
        if(i == nums.size()) return 1;
        int s;
        if(i == 0) {
            s = 0;
        } else {
            if(nums[i] <= nums[i-1]) {
                s = arr1[i-1];
            } else {
                s = arr1[i-1] + (nums[i]-nums[i-1]);
            }
        }
        int ans = 0;
        while(s <= nums[i]) {
            arr1[i] = s;
            arr2[i] = nums[i]-s;
            int a = dp[i][s] != -1 ? dp[i][s] : h(nums, arr1, arr2, i+1, dp);
            dp[i][s] = a;
            ans += a;
            ans %= 1'000'000'007;
            s++;
        }
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, -1));
        vector<int> arr1(n), arr2(n);
        return h(nums, arr1, arr2, 0, dp);
    }
};
