class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 10 ** 9 + 7
        count = 0
        @cache
        def dfs(maximum, minimum, index):
            if index == len(nums):
                return 1
            cn = 0
            for i in range(nums[index] + 1):
                x, y = i, nums[index] - i
                if x < maximum or y > minimum:
                    continue
                cn += dfs(x, y, index + 1)
            return cn

        return dfs(-inf, inf, 0) % mod
