class Solution {
public:
    const int mod=1e9+7;
    int dp[2005][52][52],n;
    int help(int i,int x,int y,vector<int> &nums){
        if(i>=n){
            return 1;
        }
        if(dp[i][x][y]!=-1){
            return dp[i][x][y];
        }
        int ans=0;
        for(int k=x;nums[i]-k>=0;k++){
            if((nums[i]-k)<=y){
                ans+=help(i+1,k,nums[i]-k,nums);
                ans%=mod;
            }

        }
        return dp[i][x][y]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        for(int i=0;i<=n;i++){
            for(int j=0;j<52;j++){
                for(int k=0;k<52;k++){
                    dp[i][j][k]=-1;
                }
            }
        }
        return help(0,0,50,nums);
    }
};
