class Solution {
public:
    vector<vector<int>> dp;
    int mod=1e9+7;
    int f(vector<int> &nums,int curr,int lasti){
        if(curr>=nums.size())return 1;
        if(dp[curr][lasti]!=-1)return dp[curr][lasti];
        int ans=0;
        for(int i=lasti;i<=nums[curr];i++){
            int arr2_curr = nums[curr] - i;
            if(curr>0 && arr2_curr>nums[curr-1]-lasti) continue;
            ans+=f(nums,curr+1,i);
            ans%=mod;
        }
        return dp[curr][lasti]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        dp.clear();
        int n=nums.size();
        dp.resize(n+1,vector<int> (51,-1));
        return f(nums,0,0)%mod;
    }
};
