class Solution {
public:

    long long dp[2005][55];
    long long mod=1000000007;
    long long solve(int i,int last1,vector<int> &nums){
        if(i==nums.size())return 1;
        if(dp[i][last1]!=-1)return dp[i][last1];
        int last2;
        if(i==0){
            last2=50;
        }
        else last2=nums[i-1]-last1;
        long long ans=0;
        for(int k=0;k<=nums[i];k++){
            if(k>=last1 && nums[i]-k<=last2){
                ans=(ans+solve(i+1,k,nums))%mod;
            }
        }
        return dp[i][last1]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return (int)solve(0,0,nums);
    }
};
