class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        mod = 10 ** 9 + 7



        @cache
        def help(a,b,i):
            if i == len(nums) - 1:
                s = max(0, min(nums[-1] - a + 1, b + 1)) % mod
                return s

            v = 0

            for index in range(a, nums[i] + 1):
                if nums[i] - index <= b:
                    v += help(index, nums[i] - index, i + 1)

            return v % mod


        return help(0,51,0) % mod
