class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums)

        dp = [[0] * (max_val + 1) for _ in range(n)]
        dp[0] = [1] * (nums[0] + 1) + [(max_val - nums[0]) * 0]


        for i in range(1, n):
            curr = nums[i]
            prefix = 0
            for j in range(0, nums[i] + 1):
                temp = 0
                for k in range(0, j + 1):
                    if (nums[i - 1] - k >= nums[i] - j):
                        temp += dp[i - 1][k]

                dp[i][j] = temp
        #print(dp)

        return sum(dp[-1]) % MOD
