/*
2,3,2

2 - 0 to 2 (3)
3 - 0 to 3 (4)
2 - 0 to 2 (2)


i = 0, pick 0 for arr1[0], arr2[0] = nums[0]
i = 1, arr1[1] can go from arr1[i-1] to nums[i], arr2[1] can go from arr2[0] to 0

arr1[i] from arr1[i-1] (prev1) to nums[i]
arr2[i] from 0 to arr2[i-1] (prev2)

if you reach final dest, then add 1
*/

class Solution {
private:
/*
arr1[i] from arr1[i-1] (prev1) to nums[i]
arr2[i] from 0 to arr2[i-1] (prev2)
50*50 = 2500*2000 = 50e5 = 5e6

2000*50*50*50

2*5*5*5 e6
250e6
2.5e8

*/
    vector<vector<vector<int>>> DP;
    int solve(vector<int>& nums, int i, int prev1, int prev2) {
        if (i >= nums.size()) return 1;
        if (DP[i][prev1][prev2] != -1) return DP[i][prev1][prev2];
        long long rv = 0, mod = 1e9+7;
        for (int curr1 = nums[i]; curr1 >= prev1; curr1--) {
            int curr2 = nums[i] - curr1;
            if (curr2 > prev2) break;
                rv += solve(nums, i+1, curr1, curr2); rv %= mod;
        }
        return (DP[i][prev1][prev2] = rv);
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        DP.resize(n,vector<vector<int>> (51, vector<int> (51,-1)));
        return solve(nums,0,0,50);
    }
};
