class Solution {
    int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(int i, int temp1, int temp2, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][temp1][temp2 + 1] != -1) { 
            return dp[i][temp1][temp2 + 1];
        }

        int opt = 0;
        for (int j = temp1; j <= nums[i]; j++) {
            if (temp2 == -1) {
                opt = (opt + solve(i + 1, j, nums[i] - j, nums)) % MOD;
            } else if (temp1 <= j && temp2 >= (nums[i] - j)) {
                opt = (opt + solve(i + 1, j, nums[i] - j, nums)) % MOD;
            }
        }

        return dp[i][temp1][temp2 + 1] = opt;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n, vector<vector<int>>(101, vector<int>(101, -1))); 
        return solve(0, 0, -1, nums);
    }
};
