class Solution {
public:
    const int MOD = 1e9 + 7;
    int dp[2001][51];

    int solve(int i, int prev, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][prev] != -1)
            return dp[i][prev];

        int ans = 0;
        for (int x = prev; x <= nums[i]; ++x) {

            int y = nums[i] - x;
            if (y <= nums[i-1]-prev) {
                ans = (ans + solve(i + 1, x, nums)) % MOD;
            }
        }

        return dp[i][prev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));

        int res = 0;
        for (int i = 0; i <= nums[0]; ++i) {
            res = (res + solve(1, i, nums)) % MOD;
        }
        return res;
    }
};
