class Solution {
public:
    using ll = long long;
    static constexpr ll M = 1'000'000'007;
    int countOfPairs(vector<int>& c) {
        int n = c.size();  // cap.
        ll dp[2][51][51];
        // initialization.
        for (int u = 0; u <= c[0]; ++u) {
            for (int v = 0; v <= c[0]; ++v) {
                dp[0][u][v] = 0;
            }
        }
        for (int u = 0; u <= c[0]; ++u) {
            int v = c[0] - u;
            dp[0][u][v] = 1;
        }
        //
        for (int i = 1; i < n; ++i) {
            int j = (i - 1) % 2;
            int k = i % 2;
            // j->k.
            // initialization.
            for (int u = 0; u <= c[i]; ++u) {
                for (int v = 0; v <= c[i]; ++v) {
                    dp[k][u][v] = 0;
                }
            }
            // dp.
            for (int u = 0; u <= c[i-1]; ++u) {
                int v = c[i-1] - u;
                if (v < 0) continue;
                if (dp[j][u][v] == 0) continue;  // skip this.
                for (int u2 = max(u, c[i] - v); u2 <= c[i]; ++u2) {
                    int v2 = c[i] - u2;
                    if (v2 > v) continue;
                    dp[k][u2][v2] = (dp[k][u2][v2] + dp[j][u][v]) % M;
                }
            }
        }
        ll ans = 0;
        int k = (n - 1) % 2;
        for (int u = 0; u <= c[n - 1]; ++u) {
            int v = c[n - 1] - u;
            if (v < 0) continue;
            ans = (ans + dp[k][u][v]) % M;
        }
        return ans;
    }
};
