class Solution {
public:
   const int MOD=1e9+7;
   vector<vector<vector<int>>> dp;
   int solve(int i,int curr1,int curr2,vector<int>&nums){
    if(i==nums.size())return 1;
    int sum=0;
    if (dp[i][curr1][curr2] != -1) return dp[i][curr1][curr2];
    for(int k=curr1;k<=nums[i];k++){
        int temp=nums[i]-k;
        if(temp<=curr2){
            sum+=solve(i+1,k,temp,nums);
            sum=sum%MOD;
        }
    }
     return dp[i][curr1][curr2] = sum;
   }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
         dp.assign(n, vector<vector<int>>(101, vector<int>(101, -1)));
         return solve(0, 0, nums[0], nums);
    }
};
