class Solution:
    MOD = int(1e9 + 7)
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_num = max(nums)  # Determine the maximum number in nums
        dp = [[0] * (max_num + 1) for _ in range(n)]  # Adjust dp array size based on the maximum number

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2_i = nums[i] - j
                dp[i][j] = 0

                for k in range(min(j, 50) + 1):
                    if arr2_i <= nums[i-1] - k and k <= 50:
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % self.MOD

        result = 0
        for j in range(nums[n-1] + 1):
            result = (result + dp[n-1][j]) % self.MOD

        return result
