#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int mod = 1e9 + 7;

    int solve(int p, int q, int i, vector<int>& nums, unordered_map<int, unordered_map<int, unordered_map<int, int>>>& dp) {
        if (i == nums.size()) return 1;  
        if (dp[p][q].count(i)) return dp[p][q][i];  

        int tk = 0;

        for (int k = 0; k <= 50; k++) {
            if (k >= p && (nums[i] - k) <= q && (nums[i] - k) >= 0) {
                tk = (tk + solve(k, nums[i] - k, i + 1, nums, dp)) % mod;
            }
        }

        return dp[p][q][i] = tk % mod;
    }

    int countOfPairs(vector<int>& nums) {
        unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp; 
        return solve(0, 50, 0, nums, dp);
    }
};
