#define ll long long int
ll mod = (ll)(1e9+7);
ll mod_add(ll a, ll b, ll m) {a = a % m; b = b % m; return (((a + b) % m) + m) % m;}
class Solution {
public:
vector<ll> v;
ll n;
vector<vector<ll>> memo;
ll recur(ll i,ll prev){
    if(i == n){
        return 1;
    }

    ll &sa = memo[prev][i];
    if(sa != -1){return sa;}
    sa = 0;
    if(i){
        ll ele = v[i-1];
        ll lstSec = (ele-prev);
        for(ll j = prev;j<=v[i];++j){
            ll rem = (v[i]-j);
            if(rem <= lstSec){
                sa = mod_add(sa,recur(i+1,j),mod);
                // sa += recur(i+1,j);
            }
        }
    }else{
        for(ll j = 0;j<=v[i];++j){
            sa = mod_add(sa,recur(i+1,j),mod);
            // sa += recur(i+1,j);
        }
    }
    return sa;
}

int countOfPairs(vector<int>& nums) {
    ll mx = 0;
    for(auto it:nums){
        v.push_back(it);
        mx = max(mx,1ll*it);
    }
    n = v.size();
    memo.assign(mx+2,vector<ll> (n+1,-1));
    return recur(0,0);
}
};
